mapper.select:
	SELECT  
   *,
      min( ROUND(  
        6378.138 * 2 * ASIN(  
            SQRT(  
                POW(  
                    SIN(  
                        (  
                            #{0} * PI() / 180 - lat * PI() / 180  
                        ) / 2  
                    ),  
                    2  
                ) + COS(#{1} * PI() / 180) * COS(lat * PI() / 180) * POW(  
                    SIN(  
                        (  
                           #{0} * PI() / 180 - lng * PI() / 180  
                        ) / 2  
                    ),  
                    2  
                )  
            )  
        ) * 1000  
    )) as distance
FROM  
   d_user
